var tab = ['Ceci est','mon second','module'];

var sayHi = function(){

    var long = process.argv.length - 1;
    var arg = process.argv[long];

    if(isNaN(arg) || arg == null){
        console.log('Au Revoir !');
    }
    else{
        for(var i = 1; i <= arg; i++){
            console.log('Bonjour ' + i + ' fois !');
        }
    }
}

